import React, { useEffect, useState } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import ArticleCard from '../components/ArticleCard';

const initialArticles = [
    {
        id: 'green-card-process',
        title: '聊聊USCIS 婚姻面谈 - 原帖在小红书',
        date: '2025-06-24',
        views: 1,
        content: '每次客人问我婚姻面谈会被问到什么问题，大概多久...（点击查看全文）',
    },
    {
        id: 'citizenship-interview',
        title: '公民入籍面谈经验',
        date: '2025-06-24',
        views: 1,
        content: '分享公民入籍面谈的流程和注意事项...（点击查看全文）',
    },
    {
        id: 'common-questions',
        title: '移民常见问题解答',
        date: '2025-06-24',
        views: 1,
        content: '针对移民过程中常见的问题进行详细解答...（点击查看全文）',
    },
    {
        id: 'all-on-xiaohongshu',
        title: '更多文章请见小红书',
        date: '2025-06-24',
        views: 0,
        content: '所有过往文章都可在小红书账号 3814236872 查看。',
    }
];

const Articles: React.FC = () => {
    const [articles, setArticles] = useState(initialArticles);

    useEffect(() => {
        window.scrollTo(0, 0);
    }, []);

    // @ts-ignore
    (window as any).updateArticleViews = function(id, views) {
        setArticles((prev) => prev.map(a => a.id === id ? { ...a, views } : a));
    };

    return (
        <div className="articles-page">
            <Header />
            <main>
                <h1>文章列表</h1>
                <div className="articles-container">
                    {articles.map((article) => (
                        <ArticleCard
                            key={article.id}
                            id={article.id}
                            title={article.title}
                            date={article.date}
                            views={article.views}
                            content={article.content}
                        />
                    ))}
                </div>
            </main>
            <Footer />
        </div>
    );
};

export default Articles;
