import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

interface ArticleCardProps {
    id: string;
    title: string;
    date: string;
    views: number;
    content: string;
}

const ArticleCard: React.FC<ArticleCardProps> = ({ id, title, date, views, content }) => {
    const navigate = useNavigate();

    // If this is the Xiaohongshu tile, do not link
    const isXiaohongshu = id === 'all-on-xiaohongshu';
    const linkPath =
        id === 'green-card-process'
            ? '/articles/green-card-process'
            : id === 'citizenship-interview'
            ? '/articles/citizenship-interview'
            : id === 'common-questions'
            ? '/articles/common-questions'
            : null;
    const cardContent = (
        <div className={`article-card${isXiaohongshu ? ' xiaohongshu-card' : ''}`}>
            <h2>{title}</h2>
            <div className="article-meta">
                <span>{date}</span>
                {views > 0 && <span> · {views} 浏览</span>}
            </div>
            <div className="article-summary">{content}</div>
        </div>
    );
    return isXiaohongshu || !linkPath ? cardContent : <Link to={linkPath}>{cardContent}</Link>;
};

export default ArticleCard;
